# -*- Perl -*-
#
# setupEcProxy.pl --
#
# Create the ECProxy configuration file
#
# Copyright (c) 2005-2012 Electric Cloud, Inc.
# All rights reserved.

use strict;
use warnings;
use ElectricCommander;
use CAMHelper;
use XML::Simple;
use Socket;

# Turn off output buffering:
$| = 1;

# ------------------------------------------------------------------------
# writeConfigFile
#
#      Writes the configuration file that is passed to ecproxy.pl
#
#
# Arguments:
#      targetHostName, the target machine's host name.
#      customization, the credentials needed to log into the target machine
#      workingDirectory, the working directory on the target machine
#      command file
#      args
#      fileName, name of config file.
#      agentDomain
# ------------------------------------------------------------------------

sub writeConfigFile($$$$$$$$) {

    my ($targetHostName, $customization, $wd, $shellCAM, $cmdFile, $fileName, $connectionType, $targetHostDomain)
        = @_;
    my $config;
    if ($connectionType ne "WINDOWS" && $connectionType ne "PSEXEC") {
        $config = {
            targetHostName   => $targetHostName,
            customization    => $customization,
            workingDirectory => $wd,
            agentCommandFile => $cmdFile,
            commandLine      => { arg => [ $shellCAM, $cmdFile ] }
        };
    }

    if ($connectionType eq "WINDOWS") {

        # NMB-27631  Some issue with Deploy Keytab on windows with Authentication Credssp
        # NMB-24295 we need CredSSP authentication only to have access to Windows AD via WinRM connection
        my $drivingHostDomain = $ENV{'USERDOMAIN'};

        my $wmiConnectExtras = '';
        if (defined($targetHostDomain) &&  defined($drivingHostDomain) && (uc($targetHostDomain) eq uc($drivingHostDomain))) {
            $wmiConnectExtras .= " -Authentication Credssp ";
        }

        $config = {
            protocol         => lc 'WMI',
            targetHostName   => $targetHostName,
            customization    => $customization,
            workingDirectory => $wd,
            agentCommandFile => $cmdFile,
            commandLine      => { arg => [ $cmdFile ] },
            wmiConnectExtras => $wmiConnectExtras

        };
    }


    # Parse the port number if it exists
    my @result = split(':', $targetHostName);

    # A port was specified
    if (@result > 1) {

        # Put the ip/host (stripped off port) in targetHostName
        $config->{targetHostName} = $result[0];

        # Put the port in targetPort
        $config->{targetPort} = $result[1];
    }

    open(FILE, '>', $fileName) || die "Unable to write $fileName: $!";
    binmode(FILE);
    print FILE XMLout(
        $config,
        NoAttr   => 1,
        RootName => "proxyConfig",
        XMLDecl  => 1
    );
    close(FILE);
}

#------------------------------------------------------------------------------
# main
#
#------------------------------------------------------------------------------

sub main() {
    my $ec = new ElectricCommander({ format => 'json' });
    my $batch = $ec->newBatch();
    my @reqIds = (
        $batch->expandString(q{$[targetHostName]}),
        $batch->expandString(q{$[/myJob/proxyCustomization]}),
        $batch->expandString(q{$[commandFile]}),
        $batch->expandString(q{$[shellCAM]}),
        $batch->expandString(q{$[ECProxyCfgFile]}),
        $batch->expandString(q{$[/myJob/connectionType]}),
        $batch->expandString(q{$[/myJob/connectionDomain]}),
    );
    $batch->submit();

    my ($targetHostName, $proxyCustomization, $commandFile, $shellCAM,
        $ECProxyCfgFile, $connectionType, $connectionDomain)
        = map {$batch->findvalue($_, "value");} @reqIds;

    if ($connectionType ne "WINDOWS") {
        writeConfigFile($targetHostName, $proxyCustomization, "/tmp",
            $shellCAM, $commandFile, $ECProxyCfgFile, $connectionType, $connectionDomain);
    }
    elsif ($connectionType eq "WINDOWS") {
        # Prepare tmp folder on remote host
        my $tempPath = '$Env:temp';
        print "Using temp dir: $tempPath for temp files.\n";

        my $targetHostNameWindows = '';

        $targetHostNameWindows = gethostbyaddr(inet_aton($targetHostName), AF_INET) or do {
            mesg("[WARNING] Failed to resolve address $targetHostName, trying to use provided hostname.");
            $targetHostNameWindows = $targetHostName;
        };

        $targetHostNameWindows =~ s/^\s+|\s+$//g;

        print "Using discoverd Windows hostname for connection: $targetHostNameWindows instead of $targetHostName.\n";

        writeConfigFile($targetHostNameWindows, $proxyCustomization, $tempPath,
            $shellCAM, $commandFile, $ECProxyCfgFile, $connectionType, $connectionDomain);
    }
    else {
        print "Invalid connection type.";
    }
}

main() unless $::gTESTING;

1;

# -*- Perl -*-
#
# spinupDeployAndConfigure.pl
#
# Copyright (c) 2005-2019 Electric Cloud, Inc.
# All rights reserved.

use warnings;
use strict;
use ElectricCommander;
use CAMHelper;
use Data::Dumper;

#------------------------------------------------------------------------------
# main
#
#------------------------------------------------------------------------------
sub main() {

    # Login to commander
    my $ec = new ElectricCommander({ format => "json" });

    # Create the batch API object
    my $batch = $ec->newBatch('parallel');

    # Create multiple requests
    my @reqIds = (
        $batch->getProperty('/myJob/hostnames'),
        $batch->getProperty('connectionCredential'),
        $batch->getProperty('connectionType'),
        $batch->getProperty('keytabFilePath'),
        $batch->getProperty('keytabName'),
        $batch->getProperty('/myJob/keytabType'),
        $batch->getProperty('/myJob/serverInstallationDirectory'),
        $batch->getProperty('/myJob/restartRequired'),
        $batch->getProperty('/myJob/targetServerOperationSystem'),
    );

    $batch->submit();

    # Retrieve the formal parameter to the "Install Agent" procedure.

    my $hostnames = $batch->findvalue($reqIds[0], 'property/value');
    my $connectionCredential = $batch->findvalue($reqIds[1], 'property/value');
    my $connectionType = $batch->findvalue($reqIds[2], 'property/value');
    my $keytabFilePath = $batch->findvalue($reqIds[3], 'property/value');
    my $keytabName = $batch->findvalue($reqIds[4], 'property/value');
    my $keytabType = $batch->findvalue($reqIds[5], 'property/value');
    my $serverInstallationDirectory = $batch->findvalue($reqIds[6], 'property/value');
    my $restartRequired = $batch->findvalue($reqIds[7], 'property/value');
    my $targetServerOperationSystem = $batch->findvalue($reqIds[8], 'property/value');

    # Logging
    $ec->setProperty("/kerberosKeytabs/$keytabName/ec_deployments/LastDeployment/TargetServerInstallationDirectory",
        {value => $serverInstallationDirectory});

    $ec->setProperty("/kerberosKeytabs/$keytabName/ec_deployments/LastDeployment/TargetServerOperationSystem",
        {value => $targetServerOperationSystem});

    $ec->setProperty("/kerberosKeytabs/$keytabName/ec_deployments/LastDeployment/TargetServerHosts",
        {value => $hostnames});


    # Building command for remote execution
    my $pathsep = "/";
    my $ext = "";
    my $arg = "";
    my @extCommands = ();
    my $ecconfigure =  "";

    if ($connectionType eq "WINDOWS"){

        $pathsep = "\\";
        $ext = ".exe";

        if ($keytabType eq "FLOW_SERVER"){

            $arg = "--serverCopyKeytabFile='" . $keytabName . "'";
            if ($restartRequired eq "0") {
                $arg .= " --skipServiceRestart"
            }

        }else {

            $arg = "--webCopyKeytabFile='" . $keytabName . "'";
            if ($restartRequired eq "1"){
                push (@extCommands, "Restart-Service -Name CommanderApache");
            }
        }
        #Remove keytab file
        push (@extCommands, "Remove-Item '$keytabName'");
        $ecconfigure .= "&";
    }else {

        if ($keytabType eq "FLOW_SERVER"){

            $arg = "--serverCopyKeytabFile='/tmp/" . $keytabName . "'";
            if ($restartRequired eq "0") {
                $arg .= " --skipServiceRestart"
            }

        }else {

            $arg = "--webCopyKeytabFile='/tmp/" . $keytabName . "'";
            if ($restartRequired eq "1"){
                push (@extCommands, "/etc/init.d/commanderApache restart");
            }
        }
        #Remove keytab file
        push (@extCommands, "rm '/tmp/$keytabName'");
    }

    $ecconfigure .= "'". $serverInstallationDirectory . $pathsep . "bin" . $pathsep . "ecconfigure" . $ext . "' " . $arg;

    my $extCommand = join(";", @extCommands);

    my $commandToRun = $ecconfigure . ";" . $extCommand;

    my $connectionUser = $ec->getFullCredential('connectionCredential', { value => 'userName' })->findvalue('//userName');

    my $password = "";
    if ("$connectionCredential" ne "") {

        $password =
            $ec->getFullCredential('connectionCredential', { value => 'password' })
                ->findvalue('//password');
    }

    mesg("[INFO] commandToRun: $commandToRun \n");
    # Put each host name into an array
    my @hostArray = getHostNames($hostnames);
    mesg("[INFO] Hosts pointed for keytab deploy: $hostnames \n");


    foreach my $targetHostName (@hostArray) {
        $ec->createJobStep(
            {
                jobStepName     => "Deploy Keytab - $targetHostName",
                parallel        => 1,
                subprocedure    => 'Deploy and Configure Keytab',
                resourceName    => '$[drivingResource]',
                actualParameter => [
                    {
                        actualParameterName => 'commandFile',
                        value               => "command-$targetHostName-"
                            . $ENV{COMMANDER_JOBSTEPID}
                    },
                    {
                        actualParameterName => 'ECProxyCfgFile',
                        value               => "cfg-$targetHostName-"
                            . $ENV{COMMANDER_JOBSTEPID}
                    }
                    ,
                    {
                        actualParameterName => 'targetHostName',
                        value               => $targetHostName
                    }
                    ,
                    {
                        actualParameterName => 'connectionType',
                        value               => $connectionType
                    },
                    {
                        actualParameterName => 'keytabFilePath',
                        value               => $keytabFilePath
                    },
                    {
                        actualParameterName => 'commandToRun',
                        value               => $commandToRun
                    },
                    {
                        actualParameterName => 'connectionCredential',
                        value               => $connectionCredential
                    },
                    {
                        actualParameterName => 'attemptingMessage',
                        value               => 'Attempting to run remove command(s)'
                    }
                ],
                credential      => [
                    {
                        credentialName => 'connectionCredential',
                        userName       => $connectionUser,
                        password       => $password
                    }
                ]
            }
        );
    }

}

main() unless $::gTESTING;

1;

# -*- Perl -*-
#
# transferToLinux.pl
#
# Copyright (c) 2005-2019 Electric Cloud, Inc.
# All rights reserved.

use strict;
use warnings;
use Net::SSH2;
use Time::HiRes qw(time);
use ElectricCommander;
use CAMHelper;
# Turn off output buffering:
$| = 1;

#------------------------------------------------------------------------------
# main
#
# Performs batch
#
#------------------------------------------------------------------------------


sub main() {
    # Create SSH2 object
    my $ssh2 = Net::SSH2->new();

    my $ec = new ElectricCommander();

    # Create the batch API object
    my $batch = $ec->newBatch('parallel');

    # Create multiple requests
    my @reqIds = (
        $batch->getProperty('targetHostName'),
        $batch->getProperty('connectionCredential'),
        $batch->getProperty('/myJob/keytabFilePath'),
        $batch->getProperty('/myJob/keytabName'),
        $batch->getProperty('/myJob/connectionPublicKey'),
        $batch->getProperty('/myJob/connectionPrivateKey'),
        $batch->getProperty('/myJob/connectionType')
    );

    $batch->submit();

    # Fetch the values
    my $targetHostName = $batch->findvalue($reqIds[0], 'property/value')->value;
    my $connectionCredential = $batch->findvalue($reqIds[1], 'property/value')->value;
    my $keytabFilePath = $batch->findvalue($reqIds[2], 'property/value')->value;
    my $keytabName = $batch->findvalue($reqIds[3], 'property/value')->value;
    my $connectionPublicKey  = $batch->findvalue($reqIds[4], 'property/value')->value;
    my $connectionPrivateKey = $batch->findvalue($reqIds[5], 'property/value')->value;
    my $connectionType = $batch->findvalue($reqIds[6], 'property/value')->value;

    mesg("Credential is  $connectionCredential \n");
    my $targetPathToInstaller = "/tmp/" . $keytabName;

    my $password = "";
    if ("$connectionCredential" ne "") {
        mesg("Attempting to retrieve password\n");
        $password = $ec->getFullCredential('connectionCredential', { value => 'password' })->findvalue('//password')->value();
        mesg("SUCCESS: Retrieved password\n");
    }

    my $connectionUser = $ec->getFullCredential('connectionCredential', { value => 'userName' })->findvalue('//userName');
    if ("$connectionType" eq "SSH_PASSWORD") {
        $ssh2 = connectToHostSSHUserPass($ssh2, $targetHostName, $connectionUser, $password);
    }
    else {
        # Connect and authenticate with target host
        $ssh2 = connectToHostSSHKeys($ssh2, $targetHostName, $connectionUser, $connectionPublicKey, $connectionPrivateKey, $password);
    }

    # Perform file transfer
    transferFileToTarget($ssh2, $keytabFilePath, $targetPathToInstaller);

    # Cleanly disconnect ssh connection
    $ssh2->disconnect();
}

#------------------------------------------------------------------------------
# connectToHostSSHKeys
#
#       Connects to targetHost and performs authentication
#
# Arguments:
#       ssh2            - A Net::SSH2 instance.
#       targetHostName  - hostname we are SSH'ing into
#       sshUser         - The SSH user
#       sshPublicKey    - SSH user's public key.
#       sshPrivateKey   - SSH user's private key.
#       sshPassPhrase   - Passphrase protecting the SSH user's private key.
# Returns:
# Net::SSH2 object connected and authenticated to target host
#------------------------------------------------------------------------------

sub connectToHostSSHKeys($$$$$$) {
    my ($ssh2, $targetHostName, $sshUser, $sshPublicKey, $sshPrivateKey, $sshPassphrase) = @_;

    # Connect to host
    mesg("Attempting to connect via SSH to $targetHostName\n");
    $ssh2->connect("$targetHostName") or die "FAILED: Cannot connect to $targetHostName\n";
    mesg("SUCCESS: Connected via SSH to $targetHostName\n");

    mesg("Attempting to authenticate via SSH key based authentication to $targetHostName\n");
    mesg("Using private key: $sshPrivateKey\n");
    mesg("Using public key: $sshPublicKey\n");
    mesg("SSH User is: $sshUser\n");

    if ($ssh2->auth_publickey($sshUser, $sshPublicKey, $sshPrivateKey, $sshPassphrase)) {
        if ($ssh2->auth_ok) {
            mesg("SUCCESS: SSH Authorization successful\n");
            return $ssh2;
        }
    }
    $ssh2->disconnect();
    die "FAILED: SSH Authorization failed\n";
}

#------------------------------------------------------------------------------
# connectToHostSSHUserPass
#
#       Connects to targetHost and performs authentication
#
# Arguments:
#       ssh2            - A Net::SSH2 instance.
#       targetHostName  - hostname we are SSH'ing into
#       sshUser         - The SSH user
#       sshPassword     - Password for the SSH User
# Returns:
# Net::SSH2 object connected and authenticated to target host
#------------------------------------------------------------------------------

sub connectToHostSSHUserPass($$$$) {
    my ($ssh2, $targetHostName, $sshUser, $sshPassword) = @_;

    mesg("Attempting to connect via SSH to $targetHostName\n");
    # Connect to host
    $ssh2->connect("$targetHostName") or die "FAILED: Cannot connect to $targetHostName\n";
    mesg("SUCCESS: Connected via SSH to $targetHostName\n");

    mesg("Attempting to authenticate via password based authentication to $targetHostName\n");
    mesg("SSH User is: $sshUser\n");
    if ($ssh2->auth_password($sshUser, $sshPassword)) {
        if ($ssh2->auth_ok) {
            mesg("SUCCESS: SSH Authorization successful\n");
            return $ssh2;
        }
    }
    $ssh2->disconnect();
    die "FAILED: SSH Authorization failed\n";
}


#------------------------------------------------------------------------------
# transferFileToTarget
#
# Transfers file from local system to remote system using scp.
#
# Arguments:
#       ssh2 -                - A Net::SSH2 instance.
#       hostPathToInstaller   - A path including file name to the file to be transferred
#       targetPathToInstaller - The destination path including file name
#                               where the file should end up
#------------------------------------------------------------------------------

sub transferFileToTarget($$$) {
    my ($ssh2, $hostPathToInstaller, $targetPathToInstaller) = @_;

    # Sanity check, does the file exist?
    if (!-e "$hostPathToInstaller") {
        $ssh2->disconnect();
        die "FAILED: Could not find file: $hostPathToInstaller\n";
    }
    mesg("File: $hostPathToInstaller\n");
    mesg("Transferring on target server: $targetPathToInstaller\n");

    # Determine size of file in bytes
    my $sizeOfFile = -s "$hostPathToInstaller";

    # Convert sizeOfFile to megabytes (1024 * 1024) = 1048576
    $sizeOfFile = $sizeOfFile / 1048576;

    # Start stopwatch
    my $start = time();

    # Perform scp put operation to target machine
    $ssh2->scp_put("$hostPathToInstaller", "$targetPathToInstaller") or die ("Transferring error");

    # Stop stopwatch
    my $end = time();

    # Report statistics

    mesg("Transferred file $hostPathToInstaller" . ' of size %.2f MB in %.2f' . " seconds" . "\n", $sizeOfFile, $end - $start);

    # Compute mb/s
    if ($end - $start > 0) {
        my $mbPerSec = $sizeOfFile / ($end - $start);
        printf('File transfer rate: %.2f MB/s', $mbPerSec);
    }
}

main() unless $::gTESTING;

1;

# -*- Perl -*-
