    use ElectricCommander::PropMod qw(/myProject);
    require "transferToLinux";
}
else {
    use ElectricCommander::PropMod qw(/myProject);
    require "transferToWindows";
}

sub writeFile($$) {
    my ($fileName, $data) = @_;
    my $f;
    if (open($f, "> $fileName")) {
        binmode $f;
        printf($f "%s", $data);
        if (close($f)) {
            return 1;
        }
    }

    # Write failed.
    return;
}

