use warnings;
use strict;
use ElectricCommander;
use Data::Dumper;
use Cwd;

#------------------------------------------------------------------------------
# main
#
#------------------------------------------------------------------------------
sub main() {

    # Login to commander
    my $ec = new ElectricCommander({ format => "json" });
    my $batch = $ec->newBatch('parallel');


    my @reqIds = (
        $batch->getProperty('/myJob/keytabName'),
        $batch->getProperty('/myJob/connectionType')
    );

    $batch->submit();

    # Retrieve the formal parameter to the "Install Agent" procedure.

    my $keytabName = $batch->findvalue($reqIds[0], 'property/value');
    my $connectionType = $batch->findvalue($reqIds[1], 'property/value');

    my $pathsep = "/";
    if ($connectionType eq "WINDOWS") {
        $pathsep = "\\";
    }

    # Logging
    my $curDir = getcwd();
    my $keytabFilePath = $curDir . $pathsep. $keytabName;

    $ec->getKerberosKeytab($keytabName,  {keytabFile=>$keytabName, download=>1});
    $ec->setProperty("/myJob/keytabFilePath", {value => $keytabFilePath});
}

main() unless $::gTESTING;

1;

